(109, 1, '2016-10-20 17:34:17', '2016-10-20 17:34:17', '<pre>function custom_login_form( $args ) {\r\n	$redirect_url = \'https://rcdesing.com\';\r\n	$defaults = shortcode_atts( array(\r\n		\'echo\'              => false,\r\n		\'redirect\'          =>  $redirect_url,\r\n		\'form_id\'           => \'loginform\',\r\n		\'label_username\'    => \'Username\',\r\n		\'label_password\'    => \'Password\',\r\n		\'label_remember\'    => \'Remember Me\',\r\n		\'label_log_in\'      => \'Log In\',\r\n		\'id_username\'       => \'user_login\',\r\n		\'id_password\'       => \'user_pass\',\r\n		\'id_remember\'       => \'rememberme\',\r\n		\'id_submit\'         => \'wp-submit\',\r\n		\'remember\'          => true,\r\n		\'value_username\'    => NULL,\r\n		\'value_remember\'    => false\r\n	), $args );\r\n	$login_args = wp_parse_args( $args, $defaults );\r\n	return wp_login_form( $login_args );\r\n}\r\nadd_shortcode( \'login_form\', \'custom_login_form\' );</pre>', 'Wordpress - Custom Login Form shortcode', '', 'publish', 'open', 'open', '', 'wordpress-custom-login-form-shortcode', '', '', '2019-09-16 21:31:08', '2019-09-16 21:31:08', '', 0, 'http://wiki.christinewilson.ca/?p=109', 0, 'post', '', 0),
