(170, 1, '2016-11-22 14:35:29', '2016-11-22 14:35:29', 'EXAMPLE\r\n<iframe src="//jsfiddle.net/3fshv6wx/2/embedded/result,html,css/" width="100%" height="300" frameborder="0" allowfullscreen="allowfullscreen"></iframe>\r\n\r\nHTML\r\n<pre><a class="a">Link item style</a>\r\n</pre>\r\nCSS\r\n<pre>a, .a {\r\n  text-decoration: none;\r\n  border-bottom: 1px solid #f28f2d;\r\n  //replace #f28f2d with colour of choice\r\n}\r\na:hover, a:focus, .a:hover, .a:focus {\r\n  color: #f28f2d;\r\n}\r\n</pre>', 'Link Style (a tag) Default Setup', '', 'inherit', 'closed', 'closed', '', '123-revision-v1', '', '', '2016-11-22 14:35:29', '2016-11-22 14:35:29', '', 123, 'http://wiki.christinewilson.ca/2016/11/22/123-revision-v1/', 0, 'revision', '', 0),
(171, 1, '2016-11-22 14:35:55', '2016-11-22 14:35:55', 'EXAMPLE\r\n<iframe src="//jsfiddle.net/3fshv6wx/2/embedded/result,html,css/" width="100%" height="300" frameborder="0" allowfullscreen="allowfullscreen"></iframe>\r\n\r\nHTML\r\n<pre><a class="a">Link item style</a>\r\n</pre>\r\nCSS\r\n<pre>a, .a {\r\n  text-decoration: none;\r\n  border-bottom: 1px solid #f28f2d;\r\n  //replace #f28f2d with colour of choice\r\n  -moz-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  -webkit-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n}\r\na:hover, a:focus, .a:hover, .a:focus {\r\n  color: #f28f2d;\r\n}\r\n</pre>', 'Link Style (a tag) Default Setup', '', 'inherit', 'closed', 'closed', '', '123-revision-v1', '', '', '2016-11-22 14:35:55', '2016-11-22 14:35:55', '', 123, 'http://wiki.christinewilson.ca/2016/11/22/123-revision-v1/', 0, 'revision', '', 0),
(172, 1, '2016-11-23 16:23:33', '2016-11-23 16:23:33', 'EXAMPLE\r\n<iframe src="//jsfiddle.net/xw120nak/7/embedded/result,js,html/" width="100%" height="300" frameborder="0" allowfullscreen="allowfullscreen"></iframe>\r\n\r\nHTML\r\n<pre><a class="tel">19054567890</a>\r\n</pre>\r\nJS\r\n<pre>\r\njQuery(function($) {\r\n  $(document).ready(function() {\r\n    var href = $(".tel").attr("href");\r\n    if (href == "" || href == null) {\r\n      href = $(".tel").text();\r\n      href = href.replace(/\\s+/g, \'\');\r\n      href = href.replace(/[{()}]/g, \'\');\r\n      href = href.replace(/-/g, \'\');\r\n    }\r\n    if (href.indexOf("tel") < 0) {\r\n      $(".tel").attr("href", ("tel:" + href));\r\n    }\r\n\r\n  });\r\n});\r\n</pre>\r\n\r\nCSS\r\n<pre>\r\na, .a {\r\n  text-decoration: none;\r\n  border-bottom: 1px solid #f28f2d;\r\n  //replace #f28f2d with colour of choice\r\n  -moz-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  -webkit-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n}\r\n\r\na:hover, a:focus, .a:hover, .a:focus {\r\n  color: #f28f2d;\r\n}\r\n\r\n</pre>', 'Auto Tel Link (a tag In The Current Version of Framework)', '', 'inherit', 'closed', 'closed', '', '140-revision-v1', '', '', '2016-11-23 16:23:33', '2016-11-23 16:23:33', '', 140, 'http://wiki.christinewilson.ca/2016/11/23/140-revision-v1/', 0, 'revision', '', 0),
