(197, 1, '2016-12-05 16:34:18', '2016-12-05 16:34:18', 'EXAMPLE\r\n<iframe src="//jsfiddle.net/xw120nak/7/embedded/result,js,html/" width="100%" height="300" frameborder="0" allowfullscreen="allowfullscreen"></iframe>\r\n\r\nHTML\r\n<pre><a class="tel">19054567890</a>\r\n</pre>\r\nJS\r\n<pre>\r\njQuery(function($) {\r\n  $(document).ready(function() {\r\n    var href = $(".tel").attr("href");\r\n    if (href == "" || href == null) {\r\n      href = $(".tel").text();\r\n      href = href.replace(/\\s+/g, \'\');\r\n      href = href.replace(/[{()}]/g, \'\');\r\n      href = href.replace(/-/g, \'\');\r\n      href = href.replace(/./g, \'\');\r\n    }\r\n    if (href.indexOf("tel") < 0) {\r\n      $(".tel").attr("href", ("tel:" + href));\r\n    }\r\n\r\n  });\r\n});\r\n</pre>\r\n\r\nCSS\r\n<pre>\r\na, .a {\r\n  text-decoration: none;\r\n  border-bottom: 1px solid #f28f2d;\r\n  //replace #f28f2d with colour of choice\r\n  -moz-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  -webkit-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n}\r\n\r\na:hover, a:focus, .a:hover, .a:focus {\r\n  color: #f28f2d;\r\n}\r\n\r\n</pre>', 'Auto Tel Link (a tag In The Current Version of Framework)', '', 'inherit', 'closed', 'closed', '', '140-revision-v1', '', '', '2016-12-05 16:34:18', '2016-12-05 16:34:18', '', 140, 'http://wiki.christinewilson.ca/2016/12/05/140-revision-v1/', 0, 'revision', '', 0),
(198, 1, '2016-12-05 16:52:48', '2016-12-05 16:52:48', 'EXAMPLE\r\n<iframe src="//jsfiddle.net/xw120nak/7/embedded/result,js,html/" width="100%" height="300" frameborder="0" allowfullscreen="allowfullscreen"></iframe>\r\n\r\nHTML\r\n<pre><a class="tel">19054567890</a>\r\n</pre>\r\nJS\r\n<pre>\r\njQuery(function($) {\r\n  $(document).ready(function() {\r\n    $(".tel").each(function () {\r\n	var href = "";\r\n	href = $(this).attr("href");\r\n	if (href == "" || href == null) {\r\n		href = $(this).text();\r\n		href = href.replace(/\\s+/g, \'\');\r\n		href = href.replace(/[{()}]/g, \'\');\r\n		href = href.replace(/-/g, \'\');\r\n		href = href.replace(/\\./g, \'\');\r\n	}\r\n	if (href.indexOf("tel") < 0) {\r\n		$(this).attr("href", ("tel:" + href));\r\n	}\r\n	});\r\n\r\n  });\r\n});\r\n</pre>\r\n\r\nCSS\r\n<pre>\r\na, .a {\r\n  text-decoration: none;\r\n  border-bottom: 1px solid #f28f2d;\r\n  //replace #f28f2d with colour of choice\r\n  -moz-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  -webkit-transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n  transition: 0.5s opacity ease, 0.25s color ease, 0.5s background-image ease, 0.25s background-color ease, 0.5s border-color ease;\r\n}\r\n\r\na:hover, a:focus, .a:hover, .a:focus {\r\n  color: #f28f2d;\r\n}\r\n\r\n</pre>', 'Auto Tel Link (a tag In The Current Version of Framework)', '', 'inherit', 'closed', 'closed', '', '140-revision-v1', '', '', '2016-12-05 16:52:48', '2016-12-05 16:52:48', '', 140, 'http://wiki.christinewilson.ca/2016/12/05/140-revision-v1/', 0, 'revision', '', 0),
